import React, { useState, useEffect } from 'react';
import axios from 'axios';
import QRCode from 'qrcode.react';

function AdminDashboard() {
  const [checkpoints, setCheckpoints] = useState([]);
  const [newCheckpoint, setNewCheckpoint] = useState({ name: '', location: { latitude: '', longitude: '' } });

  useEffect(() => {
    axios.get('/api/checkpoints').then((response) => {
      setCheckpoints(response.data);
    });
  }, []);

  const handleAddCheckpoint = () => {
    axios.post('/api/checkpoints', newCheckpoint).then((response) => {
      setCheckpoints([...checkpoints, response.data]);
    });
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <div>
        <h2>Add Checkpoint</h2>
        <input
          type="text"
          placeholder="Name"
          value={newCheckpoint.name}
          onChange={(e) => setNewCheckpoint({ ...newCheckpoint, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Latitude"
          value={newCheckpoint.location.latitude}
          onChange={(e) => setNewCheckpoint({ ...newCheckpoint, location: { ...newCheckpoint.location, latitude: e.target.value } })}
        />
        <input
          type="text"
          placeholder="Longitude"
          value={newCheckpoint.location.longitude}
          onChange={(e) => setNewCheckpoint({ ...newCheckpoint, location: { ...newCheckpoint.location, longitude: e.target.value } })}
        />
        <button onClick={handleAddCheckpoint}>Add Checkpoint</button>
      </div>
      <div>
        <h2>Checkpoints</h2>
        <ul>
          {checkpoints.map((checkpoint) => (
            <li key={checkpoint._id}>
              {checkpoint.name} - <QRCode value={checkpoint.qrCode} />
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default AdminDashboard;
